#!/bin/bash
JS=/lustre/scratch115/realdata/mdt3/projects/otcoregen/jeremys

cd $JS/gwas/AD

# Get a file listing all lead SNPs (rsid and chr/pos)
Rscript $JS/src/gwas/get_ad_pd_lead_snps.R

###############################################################################
## AD
#Rscript ~/src/R/get_ensembl_LD.R --range "10:11467398..11967396" --regionsfile AD.regions.head.txt --population "1000GENOMES:phase_3:GBR" --r2 0.8
#
#submitJobs.py --MEM 2000 -j get_ensembl_LD.GBR -q yesterday \
#    -c "Rscript ~/src/R/get_ensembl_LD.R --regionsfile AD.regions.txt --population '1000GENOMES:phase_3:GBR' --r2 0.5 | gzip > AD.regions.ld.GBR.txt.gz"
#
#submitJobs.py --MEM 2000 -j get_ensembl_LD.CEU -q yesterday \
#    -c "Rscript ~/src/R/get_ensembl_LD.R --regionfile AD.regions.txt --population '1000GENOMES:phase_3:CEU' --r2 0.5 | gzip > AD.regions.ld.CEU.txt.gz"
#

sed '1d' AD.finemap.leadSNPs.txt | cut -f 3 > LD/AD.leadSNPs.forLD.txt

Rscript ~/src/R/get_ensembl_LD.R --snpid rs679515 --population "1000GENOMES:phase_3:GBR" --r2 0.8

Rscript ~/src/R/get_ensembl_LD.R --snpsfile LD/AD.leadSNPs.forLD.txt --population "1000GENOMES:phase_3:GBR" --r2 0.8 --windowsize 500 | sed 's/1000GENOMES:phase_3:GBR/GBR/g' > LD/AD.LD_SNPs.GBR.500kb.r2_0.8.txt
Rscript ~/src/R/get_ensembl_LD.R --snpsfile LD/AD.leadSNPs.forLD.txt --population "1000GENOMES:phase_3:CEU" --r2 0.8 --windowsize 500 | sed 's/1000GENOMES:phase_3:CEU/CEU/g' > LD/AD.LD_SNPs.CEU.500kb.r2_0.8.txt
cat LD/AD.LD_SNPs.GBR.500kb.r2_0.8.txt | sed 's/1000GENOMES:phase_3:GBR/GBR/g' > LD/AD.LD_SNPs.GBR.500kb.r2_0.8.2.txt
cat LD/AD.LD_SNPs.CEU.500kb.r2_0.8.txt | sed 's/1000GENOMES:phase_3:CEU/CEU/g' > LD/AD.LD_SNPs.CEU.500kb.r2_0.8.2.txt

# Merge together the SNPs from different populations
Rscript $JS/src/R/merge_LD_snps.R LD/AD.LD_SNPs.GBR.500kb.r2_0.8.txt LD/AD.LD_SNPs.CEU.500kb.r2_0.8.txt > LD/AD.LD_SNPs.r2_0.8.txt

Rscript $JS/src/gwas/filter_LD_snp_list.R AD.finemap.annotated2.txt LD/AD.LD_SNPs.r2_0.8.txt > AD.locus.missing_snps_plus_lead.txt

